import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Profile from '../Profile/ViewProfile';
import { Navbar } from 'react-bootstrap';


const NavBar = () => {
    const [profile, setProfile] = useState(null);
    const [authenticated, setAuthenticated] = useState(false)
    useEffect(() => {
        const token = localStorage.getItem('token');
        setToken(token);
        fetch("http://127.0.0.1:8000/accounts/profile/details/", {
            headers: {
                'Authorization': `Bearer ${token}`,
            },
        })
        .then(response => {
            if (response.ok) {
                setAuthenticated(true);
                return response.json().then((data) => {
                    setProfile(data);
                })
            }
            else {
                setAuthenticated(false);
            }
        })
        
    }, [])

    return (
        <nav className="navbar navbar-expand-lg fixed-top bg-body-tertiary">
            <div className="container-fluid">
                
                
            
                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <ul className="navbar-nav me-auto mb-2 mb-lg-0">

                        <li className="nav-item">
                            <Link className="nav-link active" to="/">Home</Link>
                        </li>

                        <li className="nav-item">
                            <Link className="nav-link active" to="/">My Recipe</Link>
                        </li>

                        <li className="nav-item">
                            <Link className="nav-link active" to="recipes/create-recipe">Add Recipe</Link>
                        </li>
                    </ul>
                    {authenticated ? (
                        <>
                            <img src={profile.avatar} alt="Bootstrap" width="80" height="80"/>
                            <div className="nav-item dropdown">
                                <a className="nav-link dropdown-toggle" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                User Info
                                </a>
                                <ul className="dropdown-menu">
                                    <Profile/>
                                    <li><Link className="dropdown-item" to="/accounts/profile/edit">Edit Profile</Link></li>
                                    <li><hr className="dropdown-divider"/></li>
                                </ul>
                            </div>
                            <div id = "shoppinglist" class="nav-item">
                                <Link class="nav-link active" to="/">Shopping List</Link>
                            </div>
                        </>
                    ) : (
                        <Link to="/accounts/login"><button className='Button'>Log in</button></Link> 
                    )}
                </div>
            </div>
        </nav>
    )
}
export default NavBar;
